// cc MaxVisibilityReducer Reducer for highest visibility example// vv MaxVisibilityReducerimport java.io.IOException;import org.apache.hadoop.io.IntWritable;import org.apache.hadoop.io.Text;import org.apache.hadoop.mapreduce.Reducer;public class MaxVisibilityReducerÊ extends Reducer<Text, IntWritable, Text, IntWritable> {Ê @OverrideÊ public void reduce(Text key, Iterable<IntWritable> values,Ê Ê Ê Context context)Ê Ê Ê throws IOException, InterruptedException {Ê Ê int maxValue = Integer.MIN_VALUE;Ê Ê for (IntWritable value : values) {Ê Ê Ê maxValue = Math.max(maxValue, value.get());Ê Ê }Ê Ê context.write(key, new IntWritable(maxValue));Ê }}// ^^ MaxVisibilityReducer