// cc MinTemperature Application to find the minimum temperature in the weather dataset// vv MinTemperatureimport org.apache.hadoop.fs.Path;import org.apache.hadoop.io.IntWritable;import org.apache.hadoop.io.Text;import org.apache.hadoop.mapreduce.Job;import org.apache.hadoop.mapreduce.lib.input.FileInputFormat;import org.apache.hadoop.mapreduce.lib.output.FileOutputFormat;public class MinTemperature {Ê public static void main(String[] args) throws Exception {Ê Ê if (args.length != 2) {Ê Ê Ê System.err.println("Usage: MinTemperature <input path> <output path>");Ê Ê Ê System.exit(-1);Ê Ê }Ê Ê Job job = new Job();Ê Ê job.setJarByClass(MinTemperature.class);Ê Ê job.setJobName("Min temperature");Ê Ê FileInputFormat.addInputPath(job, new Path(args[0]));Ê Ê FileOutputFormat.setOutputPath(job, new Path(args[1]));Ê Ê job.setMapperClass(MinTemperatureMapper.class);Ê Ê job.setReducerClass(MinTemperatureReducer.class);Ê Ê job.setOutputKeyClass(Text.class);Ê Ê job.setOutputValueClass(IntWritable.class);Ê Ê System.exit(job.waitForCompletion(true) ? 0 : 1);Ê }}// ^^ MinTemperature